mapper.update:
update t_question
set deleted = 0
where id = #{id}

mapper.select:
select q.*
from t_question as q,
     t_subject as s
where q.deleted = 1
  and q.question_type = #{questionType}
  and q.subject_id = #{subjectId}
  and q.subject_id = s.id
  and s.teacher_id = #{teacherId}
  and s.deleted = 1

select q.*
from t_question as q,
     t_subject as s
where q.deleted = 1
  and question_type = #{questionType}
  and q.subject_id = s.id
  and s.teacher_id = #{teacherId}
  and s.deleted = 1

select q.*
from t_question as q,
     t_subject as s
where q.deleted = 1
  and subject_id = #{subjectId}
  and q.subject_id = s.id
  and s.teacher_id = #{teacherId}
  and s.deleted = 1
