mapper.update:
update t_subject
set name  = #{name},
    image = #{img}
where id = #{id}

update t_subject
set deleted = 0
where id = #{id}

mapper.select:
SELECT s.id
FROM t_subject as s
WHERE s.subject_code=#{subjectCode}

SELECT COUNT(id) as flag
FROM t_user_subject
WHERE user_id=#{userId} and subject_id=#{subjectId}
